const languages: Record<Locale, (text: string) => JSX.Element> = {
  en: (text) => <English text={text} />,
  de: (text) => <Deutsch text={text} />,
  es: (text) => <Spanish text={text} />,
  fr: (text) => <French text={text} />
};

interface LanguageSelectorProps {
  onChange: (locale: Locale) => void;
}

const LanguageSelector = ({ onChange }: LanguageSelectorProps) => {
  const [open, setOpen] = useState(false);
  const { T, setLocale, locale } = useT();

  const closeSelector = () => {
    setOpen(false);
    setTimeout(() => document.removeEventListener('click', closeSelector));
  };

  const openSelector = () => {
    if (open) return;
    setOpen(true);
    setTimeout(() => document.addEventListener('click', closeSelector));
  };

  const selectLocale = (lang: string) => {
    setLocale(lang);
    onChange(lang as Locale);
    setOpen(false);
  };

  return (
    <div className="flex relative">
      <button
        type="button"
        onClick={openSelector}
        className="inline-flex p-2 items-center font-medium justify-center text-sm text-gray-900 dark:text-white rounded-lg cursor-pointer hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white"
      >
        {locale === 'en' && <English w={5} h={5} text={T(`locales.${locale}`)} />}
        {locale === 'de' && <Deutsch w={5} h={5} text={T(`locales.${locale}`)} />}
        {locale === 'es' && <Spanish w={5} h={5} text={T(`locales.${locale}`)} />}
        {locale === 'fr' && <French w={5} h={5} text={T(`locales.${locale}`)} />}
      </button>
      <div
        className={`${open ? null : 'hidden'} absolute shadow-xl z-50 left-32 -bottom-2 text-base list-none bg-white divide-y divide-gray-100 rounded-lg dark:bg-gray-700`}
      >
        <ul className="py-2 font-medium" role="none">
          {Object.keys(languages).map((lang) => {
            return (
              <li key={lang}>
                <span
                  tabIndex={locale === lang ? -1 : 1}
                  onClick={() => selectLocale(lang as Locale)}
                  onKeyDown={(event) => {
                    if (['Enter', 'Space'].includes(event?.code)) selectLocale(lang as Locale);
                  }}
                  className={`${locale === lang ? 'hidden' : null} cursor-pointer block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 dark:text-gray-400 dark:hover:bg-gray-600 dark:hover:text-white`}
                  role="menuitem"
                >
                  <div className="inline-flex items-center">
                    {languages[lang as Locale](T(`locales.${lang}`))}
                  </div>
                </span>
              </li>
            );
          })}
        </ul>
      </div>
    </div>
  );
};

export default LanguageSelector;
