const Deutsch = ({w, h, text}: any) => <>
    <svg className={`h-${h ?? '3.5'} w-${w ?? '3.5'} rounded-full mr-2`} aria-hidden="true" xmlns="http://www.w3.org/2000/svg" id="flag-de" viewBox="0 0 512 512"><path fill="#ffce00" d="M0 341.3h512V512H0z" /><path d="M0 0h512v170.7H0z" /><path fill="#d00" d="M0 170.7h512v170.6H0z" /></svg>
    {text ?? 'Deutsch'}
</>

const Spanish = ({w, h, text}: any) => <>
    <svg className={`h-${h ?? '3.5'} w-${w ?? '3.5'} rounded-full mr-2`} aria-hidden="true" xmlns="http://www.w3.org/2000/svg" id="flag-es" viewBox="0 0 512 512"><path fill="#c60b1e" d="M0 0h750v500H0z"/><path fill="#ffc400" d="M0 150h750v215H0z"/></svg>
    {text ?? 'Español'}
</>

const French = ({w, h, text}: any) => <>
    <svg className={`h-${h ?? '3.5'} w-${w ?? '3.5'} rounded-full mr-2`} aria-hidden="true" xmlns="http://www.w3.org/2000/svg" id="flag-fr" viewBox="0 0 640 640"><path fill="#fff" d="M0 0h640v640H0z"/><path fill="#002654" d="M0 0h213.3v640H0z"/><path fill="#ce1126" d="M426.7 0H640v640H426.7z"/></svg>
    {text ?? 'Français'}
</>

const languages: Record<Locale, (text: string) => JSX.Element> = {
    'en': (text) => <English text={text}/>,
    'de': (text) => <Deutsch text={text}/>,
    'es': (text) => <Spanish text={text}/>,
    'fr': (text) => <French text={text}/>
}

interface LanguageSelectorProps {
    onChange: (locale: Locale) => void
    T: Translator
}

const LanguageSelector = ({T, onChange}: LanguageSelectorProps) => {
    const [open, setOpen] = useState(false)
    const {setLocale, locale} = useT()

    const closeSelector = () => {
        setOpen(false)
        setTimeout(() => document.removeEventListener('click', closeSelector))
    }

    const openSelector = () => {
        if (open) return
        setOpen(true)
        setTimeout(() => document.addEventListener('click', closeSelector))
    }

    const selectLocale = (lang: string) => {
        setLocale(lang)
        onChange(lang as Locale)
        setOpen(false)
    }

    return <div className="flex relative">
        <button type="button" onClick={openSelector} className="inline-flex p-2 items-center font-medium justify-center text-sm text-gray-900 dark:text-white rounded-lg cursor-pointer hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">
            { locale === 'en' && <English w={5} h={5} text={T(`locales.${locale}`)}/> }
            { locale === 'de' && <Deutsch w={5} h={5} text={T(`locales.${locale}`)}/> }
            { locale === 'es' && <Spanish w={5} h={5} text={T(`locales.${locale}`)}/> }
            { locale === 'fr' && <French w={5} h={5} text={T(`locales.${locale}`)}/> }
        </button>
        <div className={`${open ? null : 'hidden'} absolute shadow-xl z-50 left-32 -bottom-2 text-base list-none bg-white divide-y divide-gray-100 rounded-lg dark:bg-gray-700`}>
            <ul className="py-2 font-medium" role="none">
                {
                    Object.keys(languages).map((lang) => {
                        return <li key={lang}>
                            <span 
                                tabIndex={locale === lang ? -1 : 1} 
                                onClick={() => selectLocale(lang as Locale)}
                                onKeyDown={(event) => {
                                    if (['Enter', 'Space'].includes(event?.code)) selectLocale(lang as Locale)
                                }}
                                className={`${locale === lang ? 'hidden' : null} cursor-pointer block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 dark:text-gray-400 dark:hover:bg-gray-600 dark:hover:text-white`}
                                role="menuitem"
                            >
                                <div className="inline-flex items-center">
                                    {languages[lang as Locale](T(`locales.${lang}`))}
                                </div>
                            </span>
                        </li>
                    })
                }
            </ul>
        </div>
    </div>
}

export default LanguageSelector